
public class assignment3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		   for(int i=1; i<= 5 ;i++){
               
               for(int j=0; j < i; j++){
                       System.out.print(j+1);
               }
              
               //generate a new line
               System.out.println("");
       }
      
       //create second half of pyramid
       for(int i=5; i>0 ;i--){
              
               for(int j=0; j < i; j++){
                       System.out.print(j+1);
               }
              
               //generate a new line
               System.out.println("");
       }

}

	}


